  const clickImagen = (id) => {
    const imgIndex = result.findIndex((img) => img.id === id);
    result[imgIndex].liked = ! result[imgIndex].liked;
    setResults([...result])
  }

  return (
    <div className="galeria grid-columns-5 p-3">
      {
        result.map(img => {
          return <div className="foto">
            <img key={tiny} src={tiny} />
            <Heart filled={img.liked} onclick={() => clickImagen(img.id)}/>
          </div>
          
        })
        
      }
           
   
    </div>
       
  );
}
