import "../assets/css/galeria.css";
import Heart from "./Heart";
import { useContext } from "react";
import Context from "../Context";


export default function Home() {
  const { result, setResults } = useContext(Context);

  const clickImg = (id) =>{
    const fotosIndex = result.findIndex(ele => ele.id === id)
    result[fotosIndex].liked = ! result[fotosIndex].liked
    setResults([...result])
  }

  return (
    <div className="galeria grid-columns-5 p-3">
      {
        result.map((foto) => {
          return <div className="foto">
            <img key={foto.id} src={foto.src.tiny} onClick={() => clickImg(foto.id) }/>
            <Heart filled={foto.liked}/>
          </div>

        })

      }


    </div>

  );
}
